from flask import Blueprint, request, jsonify
from flask_jwt_extended import create_access_token, create_refresh_token, jwt_required, get_jwt_identity
from marshmallow import ValidationError
from extensions import db
from models.user import User
from schemas.user import user_schema, user_login_schema

auth_bp = Blueprint('auth', __name__)

@auth_bp.route('/register', methods=['POST'])
def register():
    """Register a new user"""
    try:
        # Validate input
        user_data = user_schema.load(request.json)

        # Check if user already exists
        if User.query.filter_by(email=user_data.email).first():
            return jsonify({'message': 'User already exists'}), 400

        # Create new user
        new_user = User(
            email=user_data.email,
            name=user_data.name,
            password=user_data.password
        )

        db.session.add(new_user)
        db.session.commit()

        # Generate tokens
        access_token = create_access_token(identity=new_user.id)
        refresh_token = create_refresh_token(identity=new_user.id)

        return jsonify({
            'message': 'User registered successfully',
            'access_token': access_token,
            'refresh_token': refresh_token,
            'user': new_user.to_dict()
        }), 201

    except ValidationError as err:
        return jsonify({'errors': err.messages}), 400
    except Exception as e:
        db.session.rollback()
        return jsonify({'message': 'Registration failed'}), 500

@auth_bp.route('/login', methods=['POST'])
def login():
    """Login user"""
    try:
        # Validate input
        login_data = user_login_schema.load(request.json)

        # Find user
        user = User.query.filter_by(email=login_data['email']).first()

        if not user or not user.check_password(login_data['password']):
            return jsonify({'message': 'Invalid credentials'}), 401

        if not user.is_active:
            return jsonify({'message': 'Account is disabled'}), 401

        # Generate tokens
        access_token = create_access_token(identity=user.id)
        refresh_token = create_refresh_token(identity=user.id)

        return jsonify({
            'message': 'Login successful',
            'access_token': access_token,
            'refresh_token': refresh_token,
            'user': user.to_dict()
        }), 200

    except ValidationError as err:
        return jsonify({'errors': err.messages}), 400
    except Exception as e:
        return jsonify({'message': 'Login failed'}), 500

@auth_bp.route('/refresh', methods=['POST'])
@jwt_required(refresh=True)
def refresh():
    """Refresh access token"""
    current_user_id = get_jwt_identity()
    new_token = create_access_token(identity=current_user_id)
    return jsonify({'access_token': new_token}), 200

@auth_bp.route('/me', methods=['GET'])
@jwt_required()
def get_current_user():
    """Get current user info"""
    current_user_id = get_jwt_identity()
    user = User.query.get(current_user_id)

    if not user:
        return jsonify({'message': 'User not found'}), 404

    return jsonify({'user': user.to_dict()}), 200

from flask import Blueprint, request, jsonify
from flask_jwt_extended import jwt_required, get_jwt_identity
from marshmallow import ValidationError
from extensions import db
from models.user import User
from schemas.user import user_schema, user_update_schema

users_bp = Blueprint('users', __name__)

@users_bp.route('/profile', methods=['GET'])
@jwt_required()
def get_profile():
    """Get user profile"""
    current_user_id = get_jwt_identity()
    user = User.query.get(current_user_id)

    if not user:
        return jsonify({'message': 'User not found'}), 404

    return jsonify({'user': user.to_dict()}), 200

@users_bp.route('/profile', methods=['PUT'])
@jwt_required()
def update_profile():
    """Update user profile"""
    try:
        current_user_id = get_jwt_identity()
        user = User.query.get(current_user_id)

        if not user:
            return jsonify({'message': 'User not found'}), 404

        # Validate input
        update_data = user_update_schema.load(request.json)

        # Check if email is being changed and if it already exists
        if 'email' in update_data and update_data['email'] != user.email:
            existing_user = User.query.filter_by(email=update_data['email']).first()
            if existing_user:
                return jsonify({'message': 'Email already exists'}), 400

        # Update user fields
        for field, value in update_data.items():
            setattr(user, field, value)

        db.session.commit()

        return jsonify({
            'message': 'Profile updated successfully',
            'user': user.to_dict()
        }), 200

    except ValidationError as err:
        return jsonify({'errors': err.messages}), 400
    except Exception as e:
        db.session.rollback()
        return jsonify({'message': 'Update failed'}), 500

@users_bp.route('/', methods=['GET'])
@jwt_required()
def get_users():
    """Get all users (admin functionality)"""
    page = request.args.get('page', 1, type=int)
    per_page = request.args.get('per_page', 10, type=int)

    users = User.query.paginate(
        page=page,
        per_page=per_page,
        error_out=False
    )

    return jsonify({
        'users': [user.to_dict() for user in users.items],
        'pagination': {
            'page': page,
            'pages': users.pages,
            'per_page': per_page,
            'total': users.total
        }
    }), 200

from flask import Blueprint, request, jsonify
from flask_jwt_extended import jwt_required, get_jwt_identity
from marshmallow import ValidationError
from extensions import db
from models.data import DataModel
from schemas.data import data_schema, data_list_schema, data_create_schema, data_update_schema

data_bp = Blueprint('data', __name__)

@data_bp.route('/', methods=['GET'])
def get_data():
    """Get all data with pagination and search"""
    page = request.args.get('page', 1, type=int)
    per_page = request.args.get('per_page', 10, type=int)
    search = request.args.get('search', '')

    query = DataModel.query

    # Search functionality
    if search:
        query = query.filter(
            DataModel.title.contains(search) |
            DataModel.content.contains(search)
        )

    # Pagination
    data_items = query.paginate(
        page=page,
        per_page=per_page,
        error_out=False
    )

    return jsonify({
        'data': [item.to_dict() for item in data_items.items],
        'pagination': {
            'page': page,
            'pages': data_items.pages,
            'per_page': per_page,
            'total': data_items.total
        }
    }), 200

@data_bp.route('/<int:data_id>', methods=['GET'])
def get_data_by_id(data_id):
    """Get data by ID"""
    data_item = DataModel.query.get_or_404(data_id)
    return jsonify({'data': data_item.to_dict()}), 200

@data_bp.route('/', methods=['POST'])
@jwt_required()
def create_data():
    """Create new data"""
    try:
        current_user_id = get_jwt_identity()

        # Validate input
        data_input = data_create_schema.load(request.json)

        # Create new data item
        new_data = DataModel(
            title=data_input['title'],
            content=data_input['content'],
            user_id=current_user_id
        )

        db.session.add(new_data)
        db.session.commit()

        return jsonify({
            'message': 'Data created successfully',
            'data': new_data.to_dict()
        }), 201

    except ValidationError as err:
        return jsonify({'errors': err.messages}), 400
    except Exception as e:
        db.session.rollback()
        return jsonify({'message': 'Creation failed'}), 500

@data_bp.route('/<int:data_id>', methods=['PUT'])
@jwt_required()
def update_data(data_id):
    """Update data"""
    try:
        current_user_id = get_jwt_identity()
        data_item = DataModel.query.get_or_404(data_id)

        # Check if user owns this data
        if data_item.user_id != current_user_id:
            return jsonify({'message': 'Access denied'}), 403

        # Validate input
        update_input = data_update_schema.load(request.json)

        # Update fields
        for field, value in update_input.items():
            setattr(data_item, field, value)

        db.session.commit()

        return jsonify({
            'message': 'Data updated successfully',
            'data': data_item.to_dict()
        }), 200

    except ValidationError as err:
        return jsonify({'errors': err.messages}), 400
    except Exception as e:
        db.session.rollback()
        return jsonify({'message': 'Update failed'}), 500

@data_bp.route('/<int:data_id>', methods=['DELETE'])
@jwt_required()
def delete_data(data_id):
    """Delete data"""
    try:
        current_user_id = get_jwt_identity()
        data_item = DataModel.query.get_or_404(data_id)

        # Check if user owns this data
        if data_item.user_id != current_user_id:
            return jsonify({'message': 'Access denied'}), 403

        db.session.delete(data_item)
        db.session.commit()

        return jsonify({'message': 'Data deleted successfully'}), 200

    except Exception as e:
        db.session.rollback()
        return jsonify({'message': 'Deletion failed'}), 500
